mapper.select:
select * from user
where user_name = #{loginVo.user_name}
and pwd = #{loginVo.password}
and is_deleted = '0'

select permission from role
where role_name = #{roleName}
and company_id = #{company_id}
and is_deleted = '0'

select DISTINCT access_name
from permission
where permission_name = #{permissionName}
  and company_id = #{company_id}
  and is_deleted = '0'
